CREATE TABLE `cuentadomiciliario` (
  `USUARIO` varchar(28) NOT NULL,
  `CLAVE` varchar(10) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

CREATE TABLE `cuentausuario` (
  `USUARIO` varchar(28) NOT NULL,
  `CLAVE` varchar(10) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

CREATE TABLE `pedidos_domiciliario` (
  `DOMICILIARIO` varchar(100) NOT NULL,
  `ULTIMOPEDIDO` int(100) NOT NULL,
  `CANTPEDIDOS` int(100) NOT NULL DEFAULT 0
) ENGINE=InnoDB DEFAULT CHARSET=latin1;
